mapper.sql:
where 1 = 1


    and id = #{record.id}


    and answer_sheet_type = #{record.answerSheetType}


    and original_examination_paper_id = #{record.originalExaminationPaperId}


    and original_examination_paper_question = #{record.originalExaminationPaperQuestion}


    and examination_paper_answer = #{record.examinationpaperAnswer}


    and answerer_id = #{record.answererId}

mapper.insert:
insert into answer_sheet (id, answer_sheet_type, original_examination_paper_id, 
  original_examination_paper_question, examination_paper_answer, 
  answerer_id
  )
values (#{id,jdbcType=INTEGER}, #{answerSheetType,jdbcType=VARCHAR}, #{originalExaminationPaperId,jdbcType=INTEGER}, 
  #{originalExaminationPaperQuestion,jdbcType=VARCHAR}, #{examinationPaperAnswer,jdbcType=VARCHAR}, 
  #{answererId,jdbcType=INTEGER}
  )
