mapper.sql:
where 1 = 1


    and id = #{record.id}


    and single_choice_question = #{record.singleChoiceQuestion}


    and single_choice_option = #{record.singleChoiceOption}


    and single_choice_answer = #{record.singleChoiceAnswer}


    and examiner_id = #{record.examinerId}


    and examiner_type = #{record.examinerType}


    and language_classification_id = #{record.languageClassificationId}


    and language_classification_name = #{record.languageClassificationName}

